# config.py
import os
from typing import Dict, Any
from dotenv import load_dotenv

# Load environment variables from .env file
load_dotenv()

# Application settings
APP_NAME = "MAAP-MCP"
APP_VERSION = "1.0"
APP_DESCRIPTION = (
    "MongoDB AI Applications Program(MAAP) Model Context Protocol(MCP) App"
)
DEBUG = os.getenv("DEBUG", "False").lower() == "true"
LOGGER_SERVICE_URL = os.getenv("LOGGER_SERVICE_URL", "http://event-logger:8181")
AI_MEMORY_SERVICE_URL = os.getenv("AI_MEMORY_SERVICE_URL", "http://ai-memory:8182")
SEMANTIC_CACHE_SERVICE_URL = os.getenv(
    "SEMANTIC_CACHE_SERVICE_URL", "http://semantic-cache:8183"
)
DATA_LOADER_SERVICE_URL = os.getenv("DATA_LOADER_SERVICE_URL", "http://data-loader:8184")

# AWS Configuration
AWS_REGION = os.getenv("AWS_REGION", "us-east-1")
EMBEDDING_MODEL_ID = os.getenv("EMBEDDING_MODEL_ID", "amazon.titan-embed-text-v1")
LLM_MODEL_ID = os.getenv("LLM_MODEL_ID", "us.anthropic.claude-3-7-sonnet-20250219-v1:0")
EMBEDDING_MODEL_INPUT_TOKEN_LIMIT = int(
    os.getenv("EMBEDDING_MODEL_INPUT_TOKEN_LIMIT", "8192")
)

# MongoDB Configuration
MONGODB_URI = os.getenv("MONGODB_URI")
DB_NAME = os.getenv("DB_NAME", "maap_data_loader")
COLLECTION_NAME = os.getenv("COLLECTION_NAME", "documents")
VECTOR_SEARCH_INDEX_NAME = os.getenv(
    "VECTOR_SEARCH_INDEX_NAME", "documents_vector_search_index"
)
FULLTEXT_SEARCH_INDEX_NAME = os.getenv(
    "FULLTEXT_SEARCH_INDEX_NAME", "documents_fulltext_search_index"
)
VECTOR_DIMENSION = int(os.getenv("VECTOR_DIMENSION", "1536"))
VECTOR_SEARCH_FIELD = os.getenv("VECTOR_SEARCH_FIELD", "embedding")
FULLTEXT_SEARCH_FIELD = os.getenv("FULLTEXT_SEARCH_FIELD", "text")

TAVILY_API_KEY = os.getenv("TAVILY_API_KEY")
# Client Configuration
DEFAULT_USER_ID = os.getenv("USER_ID", "default_user")

# Create logs directory if it doesn't exist
os.makedirs("logs", exist_ok=True)


# Load MCP server configuration
MCP_CONFIG_PATH = os.getenv("MCP_CONFIG_PATH", "mcp_servers.json")
MCP_CONFIG_ENV = os.getenv("MCP_SERVERS")

# Temporal configuration
TEMPORAL_HOST = os.getenv("TEMPORAL_HOST", "temporal")
TEMPORAL_PORT = int(os.getenv("TEMPORAL_PORT", "7233"))
TEMPORAL_NAMESPACE = os.getenv("TEMPORAL_NAMESPACE", "default")
TASK_QUEUE = os.getenv("TASK_QUEUE", "maap-task-queue")

DEFAULT_SYSTEM_PROMPT = """
You are a highly capable, friendly, and knowledgeable assistant with access to various tools to extract information from MongoDB databases about any data.
Engage with the user in a conversational manner and aim to provide accurate and helpful responses to their questions. Respond well to greetings.
Utilize the all the available tools effectively to gather information, and refer to the chat history as needed. 
If the chat history shows that you did not have information earlier that does not mean you do not have it now. 
Uses the tools again and find the information.
Your final response should be in detail and written in clear, human-readable text, free from any extra special characters. 
At the end of your response, briefly list the tools you used to arrive at your answer.
Always use tool 'hybrid_search'.
"""

PLUS_IMAGE = """data:image/png;base64,"""


def get_config() -> Dict[str, Any]:
    """Get MongoDB and application configuration information."""
    return {
        # MongoDB config
        "mongodb": {
            "uri": (
                MONGODB_URI.split("@")[-1]
                if "@" in MONGODB_URI
                else "mongodb://[hidden]"
            ),
            "db_name": DB_NAME,
            "collection_name": COLLECTION_NAME,
            "vector_search_index_name": VECTOR_SEARCH_INDEX_NAME,
            "fulltext_search_index_name": FULLTEXT_SEARCH_INDEX_NAME,
            "vector_dimension": VECTOR_DIMENSION,
            "vector_search_field": VECTOR_SEARCH_FIELD,
            "fulltext_search_field": FULLTEXT_SEARCH_FIELD,
        },
        # AWS and model config
        "aws": {
            "region": AWS_REGION,
            "embedding_model_id": EMBEDDING_MODEL_ID,
            "llm_model_id": LLM_MODEL_ID,
            "embedding_model_token_limit": EMBEDDING_MODEL_INPUT_TOKEN_LIMIT,
        },
        # App settings
        "app": {
            "name": APP_NAME,
            "version": APP_VERSION,
            "description": APP_DESCRIPTION,
            "debug": DEBUG,
        },
        # Service URLs
        "services": {
            "logger": LOGGER_SERVICE_URL,
            "ai_memory": AI_MEMORY_SERVICE_URL,
            "semantic_cache": SEMANTIC_CACHE_SERVICE_URL,
            "data_loader": DATA_LOADER_SERVICE_URL,
        },
    }
